package helloworld;

public class IfSample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x,y;
		x= 10;
		y = 20;
		if (x < y)
			System.out.println("x is less then y");
		x = x*2;
		if (x==y)
			System.out.println("x is equal to y");
		x = x*2;
		if (x > y)
			System.out.println("x is greater than y");

	}

}
